        public static int contarPalabras(String sTexto, String sTextoBuscado) {
    		int contador = 0;
    		while (sTexto.indexOf(sTextoBuscado) > -1) {
    		      sTexto = sTexto.substring(sTexto.indexOf(
    		        sTextoBuscado)+sTextoBuscado.length(),sTexto.length());
    		      contador++; 
    		}
    		return contador;
    	}
